mapper.select:
SELECT A.*
FROM (
	SELECT ROW_NUMBER() OVER(ORDER BY ${order} DESC) AS No, SEQ, TITLE, CONTENT, REGI_DATE, VIEW_CNT, LIKE_CNT, SHARE_CNT, ROOM_TYPE, ROOM_SIZE, LAST_UPDATE, IMAGE, MEM_SEQ, NICKNAME, PROFILE, IMGTAG_CNT, COMMENT_CNT
	FROM post_list_view

		ROOM_TYPE LIKE #{roomType}
		AND ROOM_SIZE LIKE #{roomSize}
		AND IMGTAG_CNT > 0

	ORDER BY No
)A
No BETWEEN #{beginRow} AND #{endRow}

SELECT * FROM post_detail_view

	MEM_SEQ LIKE #{memSeq}
	AND SEQ LIKE #{seq}

LIMIT 1

SELECT COUNT(*) FROM post

	MEM_SEQ LIKE #{memSeq}
	AND SEQ LIKE #{seq}

mapper.update:
UPDATE post

	TITLE = #{title}, CONTENT = #{content}, ROOM_TYPE = #{roomType}, ROOM_SIZE = #{roomSize}, IMAGE = #{image}, LAST_UPDATE = #{lastUpdate},
	VIEW_CNT = #{viewCnt},
	LIKE_CNT = #{likeCnt},
	SHARE_CNT = #{shareCnt}

WHERE SEQ LIKE #{seq}

UPDATE imgtag
SET ITEM_TITLE=#{itemTitle}, POINT=#{position}, ITEM_SEQ=#{itemSeq}, POST_SEQ=#{postSeq}
WHERE SEQ=#{seq}

mapper.select:
SELECT A.*
FROM (
	SELECT ROW_NUMBER() OVER(ORDER BY p.SEQ DESC) AS No, p.SEQ, p.COMMENT, p.POST_SEQ ,p.MEM_SEQ, p.NICKNAME, p.PROFILE
	FROM post_comment_view p
	WHERE POST_SEQ LIKE #{seq}
) A
A.No BETWEEN #{beginRow} AND #{endRow}

SELECT ARTICLE_SEQ AS SEQ, CHK
FROM likeit
WHERE BOARD_SEQ LIKE 1 AND MEM_SEQ LIKE #{memSeq}

SELECT ARTICLE_SEQ AS SEQ, MEM_SEQ, CHK
FROM likeit

	BOARD_SEQ LIKE 1 AND MEM_SEQ LIKE #{memSeq}
	AND ARTICLE_SEQ LIKE #{seq}

mapper.update:
UPDATE likeit
SET CHK = #{check}
WHERE ARTICLE_SEQ = #{seq} AND MEM_SEQ = #{memSeq};
