mapper.select:
SELECT *
FROM post_list_view


		No BETWEEN #{beginRow} AND #{endRow}

SELECT * FROM post_detail_view
SEQ LIKE #{value}
LIMIT 1

 SELECT COUNT(*)
 FROM post


		MEM_SEQ LIKE #{memSeq}

mapper.update:
UPDATE post
TITLE = #{title}, REGI_DATE = #{regiDate}, CONTENT = #{content}, ROOM_TYPE = #{roomType}, ROOM_SIZE = #{roomSize}, IMAGE = #{image}
VIEW_CNT = #{viewCnt},
LIKE_CNT = #{likeCnt},
SHARE_CNT = #{shareCnt},
WHERE SEQ LIKE #{seq}

mapper.select:
SELECT KEYWORD
FROM hashtag

	POST_SEQ LIKE #{value}

SELECT ITEM_TITLE, POINT, ITEM_SEQ
FROM imgtag

	POST_SEQ LIKE #{value}

SELECT A.*
FROM (
	SELECT
		ROW_NUMBER() OVER(ORDER BY p.SEQ DESC) AS No, 
		p.SEQ, p.COMMENT, p.POST_SEQ ,p.MEM_SEQ, p.NICKNAME, p.PROFILE
	FROM
		post_comment_view p
	WHERE POST_SEQ LIKE #{seq}
) A


		A.No BETWEEN #{beginRow} AND #{endRow}

 SELECT COUNT(*)
 FROM comment


		ARTICLE_SEQ LIKE #{seq}
